import re

# Lire le fichier
with open('local-icons.css', 'r') as f:
    content = f.read()

# Nouveau background-image avec encodage correct
new_bg = '''  background-image: url('data:image/svg+xml;base64,');'''

# Remplacer en utilisant une expression régulière pour matcher la ligne background-image
content = re.sub(r'  background-image: url\(.*\);', new_bg, content)

# Écrire le fichier
with open('local-icons.css', 'w') as f:
    f.write(content)

print('Remplacement terminé')
